  getGiphy = () => {
    console.log("In get giphy");
    const API_KEY = process.env.REACT_APP_GIPHY_API_KEY;
    let regSearch;
    console.log(this.state.filterBy);
    if (this.state.filterBy === "search") {
      regSearch = `http://api.giphy.com/v1/gifs/search?q=${this.state.search}&api_key=${API_KEY}`;
    } else if (this.state.filterBy === "trending") {
      regSearch = `http://api.giphy.com/v1/gifs/trending?api_key=${API_KEY}`;
    } else {
      regSearch = `http://api.giphy.com/v1/gifs/random?api_key=${API_KEY}`;
    }
    console.log("regSearch", regSearch);
    axios
      .get(regSearch)
      .then((response) => {
        let data = response.data;
        let newGif, currentGif;
        currentGif = [];
        console.log("data", data.data);
        if (this.state.filterBy === "random") {
          currentGif.map((e) => {
            console.log(e);
            return currentGif.push(e);
          });
          console.log("ok....", data);
        } else {
          currentGif.map((e) => {
            return currentGif.push(e);
          });
        }
        console.log("currentGif", currentGif);
        newGif = currentGif.map((e) => {
          return {
            title: e.title,
            imageUrl: e.images.original.url,
          };
        });

        this.setState({ result: newGif });
        console.log(this.state.result);

        /** title: d.title,
            imageUrl: d.images.original.url, */
      })
      .catch((err) => console.log(err));
  };

  getTrending = () => {};

  inputSearch = (event) => {
    this.setState({
      search: event.target.value,
      filterBy: "regular",
    });
  };

  setSearch = (e) => {
    this.setState({ search: e.target.value, filterBy: "search" });
  };

  setTrending = (e) => {
    this.setState({ filterBy: "trending" }, this.getGiphy);
  };

  setRandom = () => {
    this.setState({ filterBy: "random" }, this.getGiphy);
  };

  render() {
    return (
      <>
        <div>
          <input
            type="textarea"
            onChange={this.setSearch}
            value={this.state.search}
          />
          <button onClick={this.getGiphy}>Regular Search</button>
          <button onClick={this.setTrending}>Trending Search</button>
          <button onClick={this.setRandom}>Random Search</button>
        </div>
        <GifCard
          search={this.state.search}
          result={this.state.result}
          filterBy={this.state.filterBy}
        />
      </>
    );
  }
}

export default SearchField;
